fun RBuilder.Icon(icon: Icon, color: String = "inherit", props: BoxProps.() -> Unit = {}) {
/*    Image({
        opacity = alpha
        if (colorFilter != null) {
            filter = colorFilter
        }
        boxSize = arrayOf("24", "32")
        src=icon.resourcePath
        props()
    })*/
    Box({
        boxSize = arrayOf("24", "32")
        fill = color
        props()
    }) {
        div {
            attrs.unsafe {
                +icon.svg
            }
        }
    }
}